import React, { useEffect, useState } from 'react';
import PlugConnect from '@psychedelic/plug-connect';

import xtcDid from './idls/xtc.did';
import './App.css';

const XTC_CANISTER_ID = 'aanaa-xaaaa-aaaah-aaeiq-cai';
export const CYCLES_PER_TC = 1_000_000_000_000;

function App() {
  const [to, setTo] = useState();
  const [amount, setAmount] = useState();
  const [connected, setConnected] = useState(false);

  const handleChangeTo = e => setTo(e.target.value);
  const handleChangeAmount = e => setAmount(e.target.value);

  const safeXTCTransfer = async () => {
    // create an actor to interact with XTC
    console.log(window.ic.plug);
    const actor = await window.ic.plug.createActor(XTC_CANISTER_ID, xtcDid);

    // request a transfer using this actor
    const response = await actor.transferErc20(to, amount*CYCLES_PER_TC);
    console.log(response);
  }

  useEffect(() => {
    window.ic.plug.isConnected().then(isConnected => {
      setConnected(isConnected);
    });
  }, [])

  return (
    <div className="flex column center">
      <h1>Plug Examples</h1>
      <h2>Security Example</h2>
      <div className="plug-button">
        {connected ? 'Connected to plug' : <PlugConnect dark onConnectCallback={() => setConnected(true)} whitelist={[XTC_CANISTER_ID]} />}
      </div>
      {connected ? (
      <>
        <div className="flex column input-container">
          <label htmlFor="to">Specify recipient principal</label>
          <input name="to" type="text" onChange={handleChangeTo} value={to} />
        </div>
        <div className="flex column input-container">
          <label htmlFor="to">Specify amount of XTC</label>
          <input name="to" type="number" onChange={handleChangeAmount} value={amount} />
        </div>
        <div className="security-actions-container">
          <h3>Safe Calls</h3>
          <button type="button" onClick={safeXTCTransfer}>Transfer 0.1 XTC</button>
          {/* <button type="button" onClick={unsafeICPTransfer}>Transfer 0.01 ICP</button>
          <button type="button" onClick={unsafeNFTTransfer}>Transfer NFT</button> */}
        </div>
      </>
      ) : ''}

    </div>
  );
}

export default App;
