import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [to, setTo] = useState();
  const [amount, setAmount] = useState();

  const handleChangeTo = e => setTo(e.target.value);
  const handleChangeAmount = e => setAmount(e.target.value);

  return (
    <div className="App">
      <h1>Plug Examples</h1>
      <h2>Security Example</h2>

      <div className="flex column">
        <label htmlFor="to">Specify recipient principal</label>
        <input name="to" type="text" onChange={handleChangeTo} />
      </div>
      <div className="flex column">
        <label htmlFor="to">Specify amount</label>
        <input name="to" type="text" onChange={handleChangeAmount} />
      </div>
      <h3>Safe Calls</h3>
      {/* <div className="security-actions-container">
